mapper.delete:
delete from tz_basket where user_id = #{userId} and
(
    basket_id in

        #{basketId}

    or parent_basket_id in

        #{basketId}

)

mapper.select:
select user_id from tz_basket
where prod_id in

    #{prodId}

SELECT DISTINCT user_id FROM `tz_basket` WHERE combo_id = #{comboId}

AND sku_id IN

        #{skuId}
