mapper.select:
SELECT * FROM (
    SELECT p.prop_id,p.rule
    FROM tz_prod_prop p

        LEFT JOIN tz_prod_prop_lang ppl ON p.prop_id = ppl.prop_id

    WHERE p.rule = #{prodProp.rule} and p.shop_id = #{prodProp.shopId}

        and trim(replace(ppl.prop_name,' ','')) like trim(replace(concat('%',#{prodProp.propName},'%'),' ',''))

    LIMIT #{adapter.begin} , #{adapter.size}
) pp
left join (
SELECT ppv.`value_id`,ppv.`prop_id`
FROM tz_prod_prop_value ppv
) AS ppv ON pp.prop_id = ppv.prop_id

select pp.prop_id ,pp.prop_name from tz_prod_prop pp
left join tz_category_prop cp on pp.prop_id = cp.prop_id
where cp.category_id = #{categoryId}

select p.* from tz_prod_prop p
JOIN tz_prod_prop_lang pl ON p.prop_id = pl.prop_id
where pl.prop_name IN (#{propName}) and p.shop_id = #{shopId} and p.rule = #{rule}

SELECT pp.`prop_id`, pp.`rule`, pp.`shop_id`,ppv.*
FROM tz_prod_prop pp
LEFT JOIN tz_prod_prop_value ppv ON pp.prop_id = ppv.prop_id


        and pp.rule = #{prodProp.rule}


        and pp.shop_id = #{prodProp.shopId}
